import React, { useEffect, useState } from 'react'
import { FlatList, StyleSheet, Text, View } from 'react-native'
import tailwind from 'tailwind-rn'
import VagaApi from '../api/vagas';
import CandidaturaApi from '../api/candidatura';
import { Vaga } from '../models/vaga';
import { Candidatura } from '../models/candidatura';

export default function EVagaCandidaturas({ route, navigate }: any) {
    const { idVaga } = route.params;

    const [vaga, setVaga] = useState<Vaga>(new Vaga());
    const [candidaturas, setCandidaturas] = useState<Candidatura[]>([]);

    useEffect(() => {
        VagaApi.buscarPorId(idVaga).then(data => setVaga(data));
        CandidaturaApi.listarPorVaga(idVaga).then(data => setCandidaturas(data))
    }, [])

    return (
        <View>
            <FlatList
                data={vaga.candidatura}
                keyExtractor={item => item.idCandidatura!.toString()}
                ListHeaderComponent={
                    <View style={tailwind("mt-4 mb-6")}>
                        <Text style={tailwind("text-center text-xl")}>{vaga.titulo}</Text>
                        <Text style={tailwind("text-center text-xs")}>{vaga.idEmpresaNavigation?.nomeFantasia}</Text>
                    </View>
                }
                renderItem={({ item }) => (
                    <Card style={tailwind("mb-2")}>
                            {/* Conteudo de cima */}
                            <View style={tailwind("flex-row")}>
                                <Image source={{ uri: `${API_URL}Empresa/Img/${item.idEmpresa}` }}
                                    style={{ width: 30, height: 30 }} />
                                <Text style={tailwind("ml-5")}> {item.titulo}</Text>
                            </View>
                            <Hr style={tailwind("my-2")} />
                            {/* Conteudo debaixo */}
                            <View style={tailwind("flex-row")}>
                                <View style={tailwind("w-1/2 mr-3")}>
                                    <View style={tailwind("flex-row")}>
                                        <MaterialCommunityIcons name="currency-usd" size={18}></MaterialCommunityIcons>
                                        <Text> R$ {item.salario}</Text>
                                    </View>
                                    <View style={tailwind("flex-row")}>
                                        <MaterialCommunityIcons name="map-marker-outline" size={18}></MaterialCommunityIcons>
                                        <Text> {item.idEnderecoNavigation?.localCompleto}</Text>
                                    </View>
                                    <View style={tailwind("flex-row")}>
                                        <MaterialCommunityIcons name="clock-outline" size={18}></MaterialCommunityIcons>
                                        <Text> Expira em {item.dataExpiracao}</Text>
                                    </View>
                                </View>

                                <View style={tailwind("w-1/2 flex-row flex-wrap px-3 ")}>
                                    {item.habilidadeVaga?.map(hv => {
                                        return (
                                            <Text
                                                style={tailwind("bg-gray-400 p-2 rounded-full m-1 h-8")}
                                                ellipsizeMode='tail'
                                                numberOfLines={1}
                                                key={hv.idHabilidadeVaga?.toString()}>
                                                {habilidades.find((h: Habilidade) =>
                                                    h.idHabilidade == hv.idHabilidade)?.nomeHabilidade}
                                            </Text>
                                        )
                                    })}
                                </View>
                            </View>
                        </Card>
                )} />
        </View>
    )
}

const styles = StyleSheet.create({})
