                <View style={tailwind("flex flex-1 justify-between")}>
                    {/* Parte de cima */}
                    <View>
                        <Image style={tailwind("w-full h-32")}
                            source={require('../assets/imgs/logo-dark.png')}></Image>
                        <DrawerItemList {...props} />
