                    <View style={tailwind("m-5")}>
                        <Image style={{
                            width: 200,
                            height: 50,
                            resizeMode: 'contain',
                            alignSelf: "center"
                        }}
                            source={require('../assets/imgs/logo-senai-principal.png')} />
                    </View>
                </View>
            )}
            drawerContentOptions={{
                inactiveTintColor: "#888888",
                activeTintColor: "#FFF",
                activeBackgroundColor: "",
            }}>
            {token?.jti == TipoUsuario.CANDIDATO && (
                <Drawer.Screen name="Home" component={CHome} />
            )}
            {token?.jti == TipoUsuario.EMPRESA && (
                <Drawer.Screen name="Home" component={EHome} />
            )}

        </Drawer.Navigator>
    )
}

