import React, { useEffect, useState } from 'react'
import tailwind from 'tailwind-rn'
import { Button, FlatList, Image, StyleSheet, Text, View } from 'react-native'
import { Card } from '../components/card';
import { useNavigation } from '@react-navigation/native';
import Hr from '../components/hr';
import TimeLineIcon from '../components/svgs/TimeLineIcon'
import MoneyDollarCircleLineIcon from '../components/svgs/MoneyDollarCircleLineIcon';
import MapPinLineIcon from '../components/svgs/MapPinLineIcon';
import VagaApi from '../api/vagas';
import HabilidadeApi from '../api/habilidades';
import CandidatoApi from '../api/candidatos';
import { Matching } from '../models/matching';
import { Jwt } from '../services/auth';
import { Vaga } from '../models/vaga';
import { Habilidade } from '../models/habilidade';
import { API_URL } from '../api/apisettings';
import { ScrollView } from 'react-native-gesture-handler';


export default function Vagas() {

    const [vagas, setVagas] = useState<Vaga[]>([]);
    const [habilidades, setHabilidades] = useState<Habilidade[]>([]);
    // const [matches, setMatches] = useState<Matching[]>([])
    const [filtro, setFiltro] = useState(
        {
            titulo: "",
            minSalario: 0,
            idHabilidade: 0
        }
    );

    useEffect(() => {
        VagaApi.listar().then(data => setVagas(data));
        HabilidadeApi.listar().then(data => setHabilidades(data));
        // (async () => { 
        //     CandidatoApi.buscarPorId(await Jwt().then(t => t.jti)).then(data => setMatches(data.matching!))
        // })
    }, [])

    function filtrarVagas() {
        VagaApi.listarPorFiltro(filtro.titulo, filtro.minSalario, filtro.idHabilidade)
            .then(data => setVagas(data))
    }

    const navigation = useNavigation();
    return (
        <View style={tailwind("m-2")}>
            <FlatList
                data={vagas}
                keyExtractor={item => item.idVaga!.toString()}
                ListHeaderComponent={(<Text style={tailwind("text-center text-xl mt-4 mb-6")}>Vagas</Text>)}
                renderItem={({ item }) => (
                    <Card style={tailwind("mb-3")} key={item.idVaga}>
                        <View style={tailwind("flex-row")}>
                            <Image source={{ uri: `${API_URL}Empresa/Img/${item.idVaga}` }}
                                style={{ width: 30, height: 30 }}/>
                            <Text style={tailwind("ml-5")}> {item.titulo}</Text>
                        </View>
                        <Hr style={tailwind("my-2")} />
                        <View style={tailwind("flex-row")}>
                            <View style={tailwind("w-1/2")}>
                                <View style={tailwind("flex-row")}>
                                    <MoneyDollarCircleLineIcon />
                                    <Text> R$ {item.salario}</Text>
                                </View>
                                <View style={tailwind("flex-row")}>
                                    <MapPinLineIcon style={tailwind("flex-none")} />
                                    <Text> {item.idEnderecoNavigation?.localCompleto}</Text>
                                </View>
                                <View style={tailwind("flex-row")}>
                                    <TimeLineIcon />
                                    <Text> Expira em {item.dataExpiracao}</Text>
                                </View>
                            </View>

                            <View style={tailwind("w-1/2 flex-row flex-wrap mx-2")}>
                                {item.habilidadeVaga?.map(hv => {
                                    return (
                                        <Text style={tailwind("bg-gray-400 p-2 rounded-full m-1 h-8 flex items-center")}>
                                            {habilidades.find((h: Habilidade) =>
                                                h.idHabilidade == hv.idHabilidade)?.nomeHabilidade}
                                        </Text>
                                    )
                                })}
                            </View>
                        </View>
                    </Card>
                )}
            >
            </FlatList>
        </View>
    )
}

const styles = StyleSheet.create({})
