import React, { useEffect, useState } from 'react'
import { FlatList, Image, StyleSheet, Text, View } from 'react-native'
import { Card } from '../components/card';
import tailwind from 'tailwind-rn'
import CandidatoApi from '../api/candidatos';
import { Candidato } from '../models/candidato';
import { Jwt } from '../services/auth';

import Hr from '../components/hr';
import { API_URL } from '../api/apisettings';

export default function Inscricoes() {

    const [candidato, setCandidato] = useState<Candidato>();

    useEffect(() => {
        (async () => {
            CandidatoApi.buscarPorId(await Jwt().then(t => t.jti)).then(data => setCandidato(data))
        })
    }, []);

    return (
        <View style={tailwind("m-2")}>
            <FlatList
                data={candidato?.candidatura}
                keyExtractor={item => item.idVaga!.toString()}
                ListHeaderComponent={(<Text style={tailwind("text-center text-xl mt-4 mb-6")}>Inscrições</Text>)}
                renderItem={({ item }) => (
                    <Card style={tailwind("mb-3")}>
                        {/* Conteudo de cima */}
                        <View style={tailwind("flex-row")}>
                            <Image source={{ uri: `${API_URL}Empresa/Img/${item.idVagaNavigation?.idEmpresa}` }}
                                    style={{ width: 30, height: 30 }}/>
                            <Text style={tailwind("ml-5")}> {item.idVagaNavigation?.titulo}</Text>
                        </View>
                        <Hr style={tailwind("my-2")} />
                        {/* Conteudo debaixo */}
                        <View style={tailwind("flex-row")}>
                            <View style={tailwind("w-1/2 mr-3")}>
                                <View style={tailwind("flex-row")}>
                                    <MaterialIcons name="monetization-on" size={18}></MaterialIcons>
                                    <Text> R$ {item.salario}</Text>
                                </View>
                                <View style={tailwind("flex-row")}>
                                    <MaterialIcons name="location-on" size={18}></MaterialIcons>
                                    <Text> {item.idEnderecoNavigation?.localCompleto}</Text>
                                </View>
                                <View style={tailwind("flex-row")}>
                                    <MaterialIcons name="query-builder" size={18}></MaterialIcons>
                                    <Text> Expira em {item.dataExpiracao}</Text>
                                </View>
                            </View>

                            <View style={tailwind("w-1/2 flex-row flex-wrap px-3 ")}>
                                {item.habilidadeVaga?.map(hv => {
                                    return (
                                        <Text 
                                        style={tailwind("bg-gray-400 p-2 rounded-full m-1 h-8")} 
                                        ellipsizeMode='tail'
                                        numberOfLines={1}
                                        key={hv.idHabilidadeVaga?.toString()}>
                                            {habilidades.find((h: Habilidade) =>
                                                h.idHabilidade == hv.idHabilidade)?.nomeHabilidade}
                                        </Text>
                                    )
                                })}
                            </View>
                        </View>
                    </Card>
                )}
            >
            </FlatList>
        </View>
    )
}

const styles = StyleSheet.create({})
