import AsyncStorage from '@react-native-async-storage/async-storage';
import { createDrawerNavigator, DrawerView, DrawerItemList } from '@react-navigation/drawer';
import { createStackNavigator } from '@react-navigation/stack';
import React, { useEffect, useState } from 'react'
import { StyleSheet, Switch, Text, View, Image, ActivityIndicator } from 'react-native'
import tailwind from 'tailwind-rn';
import { Jwt } from '../services/auth';
import { Color } from '../styles/global';
import { TipoUsuario } from '../utils/enums';
import { CHome } from './c-home';
import EHome from './e-home';
import Vagas from './c-vagas';
import { AppLoading } from 'expo';
import { color } from 'react-native-reanimated';

const Drawer = createDrawerNavigator();
const Stack = createStackNavigator();

export default function Master() {
    // const [darkTheme, setDarkTheme] = useState(false);
    const [token, setToken] = useState<any>();
    const [isLoading, setIsLoading] = useState<Boolean>(true)

    // useEffect(() => {
    //     AsyncStorage.setItem("darkTheme", darkTheme.toString())
    // }, [darkTheme])

    useEffect(() => {
        Jwt().then(token => {
            setToken(token);
            setIsLoading(false);
        })
    }, [])

    const visibleItems = ['HomeScreen', 'SettingsScreen', 'HelpScreen'];

    const getVisible = item => contains(item.key, visibleItems);

    const getFilteredAndStyledItems = ({ items, ...other }) => (
        <DrawerItemList
            items={filter(getVisible, items)}
            {...other}
        />
    );

    if (isLoading) {
        return (
            <View style={tailwind("flex-1 justify-center items-center")}>
                <ActivityIndicator size="large" color={Color.PRIMARY} />
            </View>
        )
    }

    return (
        <Drawer.Navigator
            drawerStyle={{ backgroundColor: Color.SECONDARY }}
            drawerContent={(props) => (
                <View style={tailwind("flex flex-1 justify-between")}>
                    {/* Parte de cima */}
                    <View>
                        <Image style={tailwind("w-full h-32")}
                            source={require('../assets/imgs/logo-dark.png')}></Image>
                        <DrawerItemList {...props} />
                        {/* <View style={tailwind("flex-row justify-between mx-5")}>
                            <Text style={{color: darkTheme ? "#BBBBBB" : "#888888"}}>Tema Escuro</Text>
                            <Switch
                                    trackColor={{ false: "#767577", true: Color.PRIMARY2 }}
                                    thumbColor={darkTheme ? Color.PRIMARY : "#f4f3f4"}
                                    onValueChange={(e) => setDarkTheme(e)}
                                    value={darkTheme}/>
                        </View> */}
                    </View>
                    {/* Footer */}
                    {/* Ã‰ necessario encapsular dentro de uma View para manter o ratio. */}
                    <View style={tailwind("m-5")}>
                        <Image style={{
                            width: 200,
                            height: 50,
                            resizeMode: 'contain',
                            alignSelf: "center"
                        }}
                            source={require('../assets/imgs/logo-senai-principal.png')} />
                    </View>
                </View>
            )}
            drawerContentOptions={{
                inactiveTintColor: "#888888",
                activeTintColor: "#FFF",
                activeBackgroundColor: "",
            }}>
            {token?.Role == TipoUsuario.CANDIDATO && (
                <Drawer.Screen name="Home" component={CHome} />
            )}
            {token?.Role == TipoUsuario.EMPRESA && (
                <Drawer.Screen name="Home" component={EHome} />
            )}
            {/* <Drawer.Screen name="Home" component={EHome} /> */}

        </Drawer.Navigator>
    )
}

const styles = StyleSheet.create({})
