import AsyncStorage from '@react-native-async-storage/async-storage';
import { useNavigation } from '@react-navigation/native';
import React, { useState } from 'react'
import { ImageBackground, Image, StyleSheet, Text, TextInput, View, Button } from 'react-native'
import tailwind from 'tailwind-rn'
import { API_URL, TOKEN_KEY } from '../api/apisettings';
import { parseJwt } from '../services/auth';
import { TipoUsuario } from '../utils/enums';


export default function Login() {


    let navigation = useNavigation();

    const [validEmail, setvalidEmail] = useState(false);
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

    function isEmailValid() {
        let pattern = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        setvalidEmail(pattern.test(String(email).toLowerCase())
        )
    }
    const fazerLogin = () => {
        const form = {
            email: email,
            senha: senha
        }

        fetch(API_URL + 'Login', {
            method: 'POST',
            body: JSON.stringify(form),
            headers: {
                'content-type': 'application/json'
            }
        })
            .then(response => {
                if (!response.ok) alert("Senha ou E-mail incorretos")
                else return response.json()
            })
            .then(dados => {
                if (dados.token !== undefined) {
                    AsyncStorage.setItem(TOKEN_KEY, dados.token);
                    let jwt = parseJwt(dados.token);
                    if (jwt.Role == TipoUsuario.EMPRESA) navigation.navigate("Home");
                    else if (jwt.Role == TipoUsuario.CANDIDATO) navigation.navigate("Home");
                } else {
                    alert("E-mail existente")
                }
            })
            .catch(error => console.error(error))
    }

    return (
        <View>
            <ImageBackground source={require('../assets/imgs/imagem.jpg')} style={styles.background}>
                <Image source={require('../assets/imgs/logo-dark.png')} style={styles.logo as any}/>
                <View>
                    <View style={styles.form}>
                        <View style={styles.inputField}>
                            <Text style={styles.inputTitle}>Digite seu email</Text>
                            <TextInput
                                style={tailwind('h-10 text-sm mx-1')}
                                textContentType="emailAddress"
                                onChangeText={e => {
                                    isEmailValid();
                                    setEmail(e);
                                }} value={email}
                                placeholder="Insira seu email" />
                        </View>
                        <View style={styles.inputField}>
                            <Text style={styles.inputTitle}>Digite sua senha</Text>
                            <TextInput
                                style={tailwind('h-10 text-sm mx-1')}
                                secureTextEntry={true}
                                onChangeText={e => setSenha(e)} value={senha}
                                placeholder="Insira sua senha" />
                        </View>
                        <View style={styles.buttonLogin}>
                            <Button
                                disabled={!validEmail || senha.length == 0}
                                title='Login'
                                color='#CD322B'
                                onPress={fazerLogin}
                            />
                        </View>
                    </View>
                </View>
            </ImageBackground>
        </View>
    )
}

const styles = StyleSheet.create({
    
    background: {
        flex: 1,
        resizeMode: 'cover',
        justifyContent: 'center',
    },
    logo: {
        height: "100%",
        width: '100%',
        maxHeight: 500,
        maxWidth: 500,
        alignSelf: 'center'
    },
    input: {
        width: '80%',
        height: 40
    },
    form: {
        backgroundColor: 'white',
        width: '80%',
        alignSelf: 'center',
        padding: 20,
        borderRadius: 5,
        marginTop: '5%',
        marginBottom: '30%'

    },
    inputField: {
        marginBottom: '8%',
        borderBottomColor: '#000',
        borderBottomWidth: 1
        // textDecorationLine: "underline",
        // textDecorationStyle: "solid",
        // textDecorationColor: "#000",
    },
    inputTitle: {
        fontSize: 25,
        fontWeight: "700",
    },
    buttonLogin: {
        width: 100,
        alignSelf: 'center',
        marginTop: 20

    }
})
