import AsyncStorage from '@react-native-async-storage/async-storage';
import { createDrawerNavigator, DrawerView, DrawerItemList } from '@react-navigation/drawer';
import { createStackNavigator } from '@react-navigation/stack';
import React, { useEffect, useState } from 'react'
import { StyleSheet, Switch, Text, View } from 'react-native'
import tailwind from 'tailwind-rn';
import { Color } from '../styles/global';
import { Home } from './home';
import Vagas from './vagas';

const Drawer = createDrawerNavigator();
const Stack = createStackNavigator();

export default function Master() {
    const [darkTheme, setDarkTheme] = useState(false);

    useEffect(() => {
        AsyncStorage.setItem("darkTheme", )
    }, [darkTheme])

    return (
        <Drawer.Navigator
            drawerStyle={{ backgroundColor: Color.SECONDARY }}
            drawerContent={(props) => (
                <View style={{ flex: 1 }}>
                    <DrawerItemList {...props} />
                    <View style={tailwind("flex-row justify-between mx-5")}>
                        <Text style={{color: darkTheme ? "#BBBBBB" : "#888888"}}>Tema Escuro</Text>
                        <Switch
                                trackColor={{ false: "#767577", true: Color.PRIMARY2 }}
                                thumbColor={darkTheme ? Color.PRIMARY : "#f4f3f4"}
                                onValueChange={(e) => setDarkTheme(e)}
                                value={darkTheme}/>
                    </View>
                </View>
            )}
                        drawerContentOptions={{
                inactiveTintColor: "#888888",
                activeTintColor: "#FFF",
                activeBackgroundColor: "",
            }}>
            <Drawer.Screen name="Home" component={Home} />
        </Drawer.Navigator>
    )
}

const styles = StyleSheet.create({})
